function App() {

  const [init, setInit] = useState(false);

  useEffect(() => {
    setInit(true);
  },[]);

  async function componentDidMount(){
    await this.initWeb3();
  }

  const initWeb3 = async () => {
    if (window.ethereum) {
      console.log('Recent mode')
      this.web3 = new Web3(window.ethereum);
      try {
          // Request account access if needed
          await window.ethereum.enable();
          // Acccounts now exposed
          // this.web3.eth.sendTransaction({/* ... */});
      } catch (error) {
          // User denied account access...
          console.log(`User denied account access error : ${error}`)
      }
    }
    // Legacy dapp browsers...
    else if (window.web3) {
      console.log('legacy mode')
      this.web3 = new Web3(Web3.currentProvider);
      // Acccounts always exposed
      // web3.eth.sendTransaction({/* ... */});
    }
    // Non-dapp browsers...
    else {
      console.log('Non-Ethereum browser detected. You should consider trying MetaMask!');
    }

    let accounts = await this.web3.eth.getAccounts();
    this.account = accounts[0];

    this.workContract = new this.web3.eth.Contract(workABI, workAddress);

    let nonce = await this.web3.eth.getTransactionCount(this.account);
    let e_address = '0x0b76503E3Cb89C20baF96347082c0eB4F77F46D9';
    await this.lotteryContract.methods.firstDepositMoney(e_address).send({from:this.account, value:5000000000000000, gas:300000})
  }

  const firstDepositMoney = async () => {
    // nonce
    let e_address = '';
    let nonce = await this.web3.eth.getTransactionCount(this.account);
    this.lotteryContract.methods.firstDepositMoney(e_address).send({from:this.account, value:5000000000000000, gas:300000, nonce:nonce})
    .on('transactionHash', (hash) =>{
      console.log(hash)
    })

  }

  return (
    <>
      {init ? 
      <ThemeProvider theme={theme}>
        <RecoilRoot>
          <GlobalStyle />
          <AppRouter/>
        </RecoilRoot> 
      </ThemeProvider>
      : 
        "Initializing..."
      }
    </>
  );
}

export default App;
